import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UserDetailsComponent } from '../user-details/user-details.component';

@Component({
  selector: 'app-singleuserinfo',
  templateUrl: './singleuserinfo.component.html',
  styleUrls: ['./singleuserinfo.component.css']
})
export class SingleuserinfoComponent {
  itemIndex:number=999;
   user:any;
  constructor(private route:ActivatedRoute,_userDetailsComponent:UserDetailsComponent) {
    this.route.paramMap.subscribe(x=>{
     this.itemIndex=x.get('id') as unknown as number;
     if(this.itemIndex!=999)
     {
      this.user=_userDetailsComponent.userDetails[this.itemIndex]
     }
    })
    
  }

}
