import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UserDetailsComponent } from '../user-details/user-details.component';

@Component({
  selector: 'app-singleuserinfo',
  templateUrl: './singleuserinfo.component.html',
  styleUrls: ['./singleuserinfo.component.css']
})
export class SingleuserinfoComponent {
  itemIndex:number=999;
  _userDetailsComponent:any;
   user:any;
   TempName:string='';
   TempEmail:String='';
   TempAddress:string='';

  constructor(private route:ActivatedRoute,userDetailsComponent:UserDetailsComponent) {
    this.route.paramMap.subscribe(x=>{
     this.itemIndex=x.get('id') as unknown as number;
     if(this.itemIndex!=999)
     {this._userDetailsComponent=userDetailsComponent;
      this.user=this._userDetailsComponent.userDetails[this.itemIndex];
      // this.TempName=this.user.Name;
      // this.TempEmail=this.user.Email;
      // this.TempAddress=this.user.Address;
      
     }
    });
    
    this.route.queryParamMap.subscribe(value=>{
      console.log(value.get('Page'));
      console.log(value.get('Orderby'));
      
    }
      )
    
  }

  UpdateUserInfo()
  {
    this.user.Name=this.TempName;
   // console.log("name"+this.TempName);
    
    this.user.Email=this.TempEmail;
    //console.log("Email"+this.TempEmail);
    this.user.Address=this.TempAddress;
    //console.log("Address"+this.TempAddress);

    //console.log(this.user); 
   this._userDetailsComponent[this.itemIndex]=this.user;

   alert("Sucessfully Updated the info");
   
  }

}
